import React, { useState } from "react";
import "./index.css";

function LoggedPage() {
    const [name, setName] = useState("");
    const [searchResult, setSearchResult] = useState("");
    const [users, setUsers] = useState([]);

    const handleOnChangeSearch = (event) => {
        const value = event.target.value;
        setName(value);

        // Retrieve data from local storage
        const retrievedData = localStorage.getItem('users');

        // Check if data exists in local storage
        if (retrievedData !== null) {
            // Data exists, parse it if needed
            const parsedData = JSON.parse(retrievedData);
            setUsers(parsedData);

            // Perform binary search to find the name
            const result = binarySearch(parsedData.map(user => user.firstName), value);
            setSearchResult(result);
        } else {
            // Data does not exist
            console.log('No data found in local storage');
        }
    };

    const binarySearch = (arr, target) => {
        let left = 0;
        let right = arr.length - 1;

        while (left <= right) {
            const mid = Math.floor((left + right) / 2);
            const firstName = arr[mid];

            if (firstName === target) {
                return firstName; // Name found
            } else if (firstName < target) {
                left = mid + 1; // Search right half
            } else {
                right = mid - 1; // Search left half
            }
        }

        return "Name not found"; // Name not found
    };
    console.log("users: " + JSON.stringify(users));

    return (
        <div className="loggedInContainer">
            <div className="loggedInName">Welcome {searchResult}</div>
            <div className="searchContainer">
                <input
                    name="nameValue"
                    type="text"
                    placeholder="name"
                    value={name}
                    onChange={handleOnChangeSearch}
                />
            </div>
            <div className="listsOfUserContainer">
                {users.map((user, index) => (
                    <div key={index} className="userItem">
                        <div>{user.firstName}</div>
                        <div>{user.lastName}</div>
                        {/* Add other user details as needed */}
                    </div>
                ))}
            </div>
            <div className="buttonContainer">
                <div className="saveBtn"><button> Save</button></div>
                <div className="deleteBtn"><button> Delete</button></div>
            </div>
        </div>
    );
}

export default LoggedPage;
